import React, { Component } from "react";
import { StyleSheet, FlatList, TouchableOpacity, Text, View, SafeAreaView, SectionList, StatusBar } from "react-native";
import { Box, Heading, Avatar, HStack, VStack, Spacer, Center, NativeBaseProvider } from "native-base";
// const DATA = [
//   {
//     title: "Main dishes",
//     data: ["Pizza", "Burger", "Risotto"]
//   },
//   {
//     title: "Sides",
//     data: ["French Fries", "Onion Rings", "Fried Shrimps"]
//   },
//   {
//     title: "Drinks",
//     data: ["Water", "Coke", "Beer"]
//   },
//   {
//     title: "Desserts",
//     data: ["Cheese Cake", "Ice Cream"]
//   }
// ];

const Item = ({ title }) => (
  // <View style={styles.item}>
  //   <Text style={styles.title}>{title}</Text>
  // </View>
  <Box
            borderBottomWidth="1"
            _dark={{
              borderColor: 'gray.600',
            }}
            borderColor="coolGray.200"
            pl="4"
            pr="5"
            py="2">
            <HStack space={3} justifyContent="space-between">

              <VStack>
                <Text
                  _dark={{
                    color: 'warmGray.50',
                  }}
                  color="coolGray.800"
                  bold>
                  {title}
                </Text>
                <Text
                  color="coolGray.600"
                  _dark={{
                    color: 'warmGray.200',
                  }}>
                  {title}
                </Text>
              </VStack>
              <Spacer />
              <Text
                fontSize="xs"
                _dark={{
                  color: 'warmGray.50',
                }}
                color="coolGray.800"
                alignSelf="flex-start">
                {title}
              </Text>
            </HStack>
          </Box>
);

class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      data: [],
    }
  }
  componentDidMount = () => {
    fetch("https://opensheet.elk.sh/1DjrkLokL7ODiY4FZH1hvlc91yi-NbOT90Jtz5furBgo/Movies", {
      "method": "GET",

    })
      .then((response) => response.json())
      .then((responseJson) => {
        console.log("Mount", responseJson[0].Category);
        this.setState({ data: responseJson });
        console.log("state check", this.state.data)
      })
      .catch((error) => {
        console.error(error);
      });
  }
  renderItemComponent = (data) =>
    <TouchableOpacity style={styles.container}>
      {/* <Image style={styles.image} source={{ uri: data.item.url }} /> */}

      <Text style={styles.title}>{data.item.URL}</Text>
    </TouchableOpacity>

  ItemSeparator = () => <View style={{
    height: 2,
    backgroundColor: "rgba(0,0,0,0.5)",
    marginLeft: 10,
    marginRight: 10,
  }}
  />
  render() {
    // console.log("Mount",responseJson[0].Category);
    return (
      <SafeAreaView>
       <NativeBaseProvider>
          <Heading>I'm a Heading</Heading>
          </NativeBaseProvider>
        <FlatList
          data={this.state.data}
          renderItem={item => this.renderItemComponent(item)}
          keyExtractor={item => item.id.toString()}
          ItemSeparatorComponent={this.ItemSeparator}
        />
      </SafeAreaView>
    );
  }
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    paddingTop: StatusBar.currentHeight,
    marginHorizontal: 16
  },
  item: {
    backgroundColor: "#f9c2ff",
    padding: 20,
    marginVertical: 8
  },
  header: {
    fontSize: 32,
    backgroundColor: "#fff"
  },
  title: {
    color: "#000",
    fontSize: 24
  }
});

export default App;
