import React, { Component } from "react";
import { StyleSheet, FlatList, TouchableOpacity, Text, View, SafeAreaView, SectionList, StatusBar } from "react-native";
import { Box, Heading, Avatar, HStack, VStack, Spacer, Center, NativeBaseProvider } from "native-base";


class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      data: [],
    }
  }
  componentDidMount = () => {
    fetch("https://opensheet.elk.sh/1DjrkLokL7ODiY4FZH1hvlc91yi-NbOT90Jtz5furBgo/Movies", {
      "method": "GET",

    })
      .then((response) => response.json())
      .then((responseJson) => {
        console.log("Mount", responseJson[0].Category);
        this.setState({ data: responseJson });
        console.log("state check", this.state.data)
      })
      .catch((error) => {
        console.error(error);
      });
  }
  renderItemComponent = (data) =>
    <TouchableOpacity style={styles.container}>
      {/* <Image style={styles.image} source={{ uri: data.item.url }} /> */}

      {/* <Text style={styles.title}>{data.item.URL}</Text> */}
      <Box
        borderBottomWidth="1"
        _dark={{
          borderColor: 'gray.600',
        }}
        borderColor="coolGray.200"
        pl="4"
        pr="5"
        py="2">
        <HStack space={3} justifyContent="space-between">

          <VStack>
            <Text
              _dark={{
                color: 'warmGray.50',
              }}
              color="coolGray.800"
              bold>
              {data.item.URL}
            </Text>
            <Text
              color="coolGray.600"
              _dark={{
                color: 'warmGray.200',
              }}>
              {data.item.URL}
            </Text>
          </VStack>
          <Spacer />
          <Text
            fontSize="xs"
            _dark={{
              color: 'warmGray.50',
            }}
            color="coolGray.800"
            alignSelf="flex-start">
            {data.item.URL}
          </Text>
        </HStack>
      </Box>
    </TouchableOpacity>

  ItemSeparator = () => <View style={{
    height: 2,
    backgroundColor: "rgba(0,0,0,0.5)",
    marginLeft: 10,
    marginRight: 10,
  }}
  />
  render() {
    // console.log("Mount",responseJson[0].Category);
    return (
      <SafeAreaView>
        <NativeBaseProvider>
        <AppBar />
          <Heading>I'm a Heading</Heading>

          <FlatList
            data={this.state.data}
            renderItem={item => this.renderItemComponent(item)}
            keyExtractor={item => item.id.toString()}
            ItemSeparatorComponent={this.ItemSeparator}
          />
        </NativeBaseProvider>
      </SafeAreaView>
    );
  }
}


const Item = () => (
 <View> 
    <StatusBar bg="#3700B3" barStyle="light-content" />
      <Box safeAreaTop bg="#6200ee" />
      <HStack bg="#6200ee" px="1" py="3" justifyContent="space-between" alignItems="center" w="100%" maxW="350">
        <HStack alignItems="center">
          <IconButton icon={<Icon size="sm" as={MaterialIcons} name="menu" color="white" />} />
          <Text color="white" fontSize="20" fontWeight="bold">
            Home
          </Text>
        </HStack>
        <HStack>
          <IconButton icon={<Icon as={MaterialIcons} name="favorite" size="sm" color="white" />} />
          <IconButton icon={<Icon as={MaterialIcons} name="search" size="sm" color="white" />} />
          <IconButton icon={<Icon as={MaterialIcons} name="more-vert" size="sm" color="white" />} />
        </HStack>
      </HStack>
      </View>

);


const styles = StyleSheet.create({
  container: {
    flex: 1,
    paddingTop: StatusBar.currentHeight,
    marginHorizontal: 16
  },
  item: {
    backgroundColor: "#f9c2ff",
    padding: 20,
    marginVertical: 8
  },
  header: {
    fontSize: 32,
    backgroundColor: "#fff"
  },
  title: {
    color: "#000",
    fontSize: 24
  }
});

export default App;
